import React from "react";
import { Link } from "react-router-dom";
import { CSSTransition } from "react-transition-group";
import { animate, delay } from "../../utils";
import styles from "./lobby.module.scss";
import NakoAPI, { LobbyInfo, parseMessage } from "../../modules/websocket-messages";
import Logo from "../../modules/logo/logo";

enum InputPhase {
  Name,
  LobbyID,
  Ended
}

enum ErrorMessage {
  Generic = "An error occurred while your request was being processed.",
  TimedOut = "Your request was timed out. Please try again.",
  MissingLobby = "The provided lobby ID does not exist."
}

interface LobbyProps {
  socketURL: string;
}

interface LobbyStates {
  name: string;
  lobbyIDInput: string;
  currentInput: InputPhase;
  inputParams: React.InputHTMLAttributes<HTMLInputElement>[];
  inputRef: React.RefObject<HTMLInputElement>;
  inputTransitioning: boolean;
  error: {
    occurred: boolean;
    message: ErrorMessage;
  };
  transitionedToLobby: boolean;
  lobbyInfo?: LobbyInfo;
}

class Lobby extends React.Component<LobbyProps & React.HTMLAttributes<HTMLDivElement>, LobbyStates> {
  static defaultProps = {
    socketURL: "ws://18.163.114.28:1880/ws"
  }

  connection: NakoAPI;

  constructor(props: LobbyProps & React.HTMLAttributes<HTMLDivElement>) {
    super(props);

    (window as any).parseMessage = parseMessage;

    this.state = {
      name: "",
      lobbyIDInput: "",
      currentInput: InputPhase.Name,
      inputParams: [{
        className: "box-shadow",
        type: "text",
        placeholder: "Enter name...",
        onChange: e => this.setState({ name: e.target.value })
      }, {
        className: "box-shadow hide-number-buttons",
        type: "number",
        placeholder: "Enter game ID",
        onChange: e => this.setState({ lobbyIDInput: e.target.value })
      }],
      inputRef: React.createRef<HTMLInputElement>(),
      inputTransitioning: false,
      error: {
        occurred: false,
        message: ErrorMessage.Generic
      },
      transitionedToLobby: false
    }

    this.connection = new NakoAPI(this.props.socketURL, {    // Constructor is ran twice due to React.StrictMode, creating two connections.
      onError: e => console.log(e)                           // This probably won't happen in production if the documentations are right.
    });                                                      // WebSocket server should disconnect the unused connection due to keep alive timeout anyway.

    this.connection.subscribe<"req", "keep_alive">("req", "keep_alive", () => this.connection.send<"res", "keep_alive">({
      method: "res",
      type: "keep_alive",
      payload: {
        name: this.state.name,
        lobbyID: this.state.lobbyInfo!.id
      }
    }));

    // this.connection = new WebSocket(this.props.socketURL);    
    // this.connection.onopen = () => {
    //   console.log(this.connection);
    //   console.log("Connected");
    // }

