    //           res = { name: this.state.name, lobbyID: this.state.lobbyInfo!.id } as KeepAliveResponse
    //           break;

    //         default:
    //           res = new Error(`Unsupported request type: ${msg.type}`);
    //           this.setState({ errorOccured: true });
    //       }

    //       if (res instanceof Error) {
    //         console.log(res, msg);
    //       } else {
    //         this.connection.send(JSON.stringify(res));
    //       }

    //     } else if (msg.method === "res") {
    //       switch (msg.type) {
    //         case "lobby_join": {
    //           const res = e.data as LobbyJoinResponse;

    //           if (res.lobbyInfo) {
    //             this.setState({ lobbyInfo: res.lobbyInfo })
    //           } else {
    //             this.lobbyNotFoundHandler(res.error!);
    //           }

    //           break;
    //         }

    //         case "lobby_leave": {
    //           const res = e.data as LobbyLeaveResponse;

    //           if (res.lobbyInfo && !res.lobbyInfo.players.includes(this.state.name)) {
    //             this.setState({ lobbyInfo: undefined });
    //           } else {
    //             console.log(res.error);
    //           }

    //           break;
    //         }

    //         case "lobby_update": {
    //           const res = e.data as LobbyUpdateResponse;
    //           this.setState({ lobbyInfo: res.lobbyInfo });
    //           break;
    //         }

    //         default: {
