    // }

    // this.connection.onerror = e => {
    //   console.log(e);
    // }
  }

  componentWillUnmount() {
    this.connection.close();
  }

  lobbyNotFoundHandler(error: string) {
    console.log(error);
  }

  async onFormSubmitHandler(e: React.FormEvent<HTMLFormElement>, ms?: number) {
    e.preventDefault();
    this.setState({ error: { ...this.state.error, occurred: false } });

    const triggerNextInputPhase = () => {
      if (this.state.currentInput < InputPhase.Ended) {
        this.setState({ currentInput: this.state.currentInput + 1 });
      }
    }

    const reject = () => {
      animate("shake", this.state.inputRef.current!, 500);
    }

    const triggerDelay = async () => {
      if (ms) {
        console.log(true);
        this.setState({
          inputTransitioning: true
        });

        await delay(ms);

        console.log(false);
        this.setState({
          inputTransitioning: false
        });
      }
    }

    switch (this.state.currentInput) {
      case InputPhase.Name:
        if (this.state.name !== "") {
          await triggerDelay();
          triggerNextInputPhase();
        } else {
          reject();
        }

        break;

      case InputPhase.LobbyID:
        if (this.state.lobbyIDInput !== "") {
          this.connection.send<"req", "lobby_join">({
            method: "req",
            type: "lobby_join",
            payload: {
              lobbyID: Number(this.state.lobbyIDInput),
              name: this.state.name
            }
          }, 10000).then(async res => {
            if (res) {
              if (res.lobbyInfo) {
                this.setState({ lobbyInfo: res.lobbyInfo })
                await triggerDelay();
                triggerNextInputPhase();
              } else {
                this.setState({ error: { occurred: true, message: ErrorMessage.MissingLobby } });
                console.log(res.error);
              }
            } else {
              this.setState({ error: { occurred: true, message: ErrorMessage.TimedOut } });
            }
          })
        } else {
          reject();
        }
        break;

      default:
        break;
    }
  }

  render() {
    const { socketURL, ...rest } = this.props;

    return (
      <div {...rest}>
        <div className={styles["lobby-container"]}>
          <Link to="/">
            <button id={styles.back} className="box-shadow">
              <span className={styles["arrow-left"]}/>
              <span>Back</span>
            </button>
          </Link>
          <button
            style={{position: "absolute", zIndex: 1, bottom: 0, right: 0}}
            onClick={() => this.setState({
              lobbyInfo: this.state.lobbyInfo ? undefined : {
                id: 0,
                players: [this.state.name, "Test1", "Test2", "Test3", "Test4", "Test5", "Test6", "Test7", "Test8", "Test9", "Test10", "Test11", "Test12"]
              }
            })}
          >
            Test
          </button>
          <CSSTransition
            in={this.state.lobbyInfo !== undefined}
            timeout={500}
            classNames={{
              enter: styles["background-top-enter"],
              enterActive: styles["background-top-enter-active"],
              enterDone: styles["background-top-enter-done"],
              exit: styles["background-top-exit"],
              exitActive: styles["background-top-exit-active"],
              exitDone: styles["background-top-exit-done"]
            }}
          >
            <div id={this.state.transitionedToLobby ? styles.background : styles["background-top"]}>
              <CSSTransition
                in={this.state.lobbyInfo === undefined} timeout={500}
                classNames={{
                  enter: styles["title-container-enter"],
                  enterActive: styles["title-container-enter-active"],
                  enterDone: styles["title-container-enter-done"],
                  exit: styles["title-container-exit"],
                  exitActive: styles["title-container-exit-active"],
                  exitDone: styles["title-container-exit-done"]
                }}
                onExited={() => this.setState({ transitionedToLobby: true })}
                mountOnEnter unmountOnExit
              >
                {/* <div id={styles["title-container"]}>
                  <h1 className="text-shadow">Nako</h1>
                  <h5 className="text-shadow">for students</h5>
                </div> */}
                <Logo id={styles["title-container"]} role="student"/>
              </CSSTransition>
              <CSSTransition
                in={this.state.transitionedToLobby && this.state.lobbyInfo !== undefined}
                timeout={500}
                classNames={{
                  enter: styles["player-list-container-enter"],
                  enterActive: styles["player-list-container-enter-active"],
                  enterDone: styles["player-list-container-enter-done"],
                  exit: styles["player-list-container-exit"],
                  exitActive: styles["player-list-container-exit-active"],
                  exitDone: styles["player-list-container-exit-done"]
                }}
                onExit={() => this.setState({ transitionedToLobby: false })}
                mountOnEnter unmountOnExit
              >
                <div id={styles["player-list-container"]}>
                  <h3>You are playing with:</h3>
                  <div id={styles["player-list"]}>
                    {this.state.lobbyInfo !== undefined ? this.state.lobbyInfo!.players.map((player, i) => <p key={i}>{player}</p>) : null}
                  </div>
                </div>
              </CSSTransition>
            </div>
          </CSSTransition>
          <CSSTransition
            in={this.state.lobbyInfo === undefined}
            timeout={500}
            classNames={{
              enter: styles["background-bottom-enter"],
              enterActive: styles["background-bottom-enter-active"],
              enterDone: styles["background-bottom-enter-done"],
              exit: styles["background-bottom-exit"],
              exitActive: styles["background-bottom-exit-active"],
              exitDone: styles["background-bottom-exit-done"]
            }}
            unmountOnExit
          >
            <div id={styles["background-bottom"]} className="box-shadow-inner">
              <form id={styles["input-container"]} onSubmit={e => this.onFormSubmitHandler(e, 600)}>
                <CSSTransition in={!this.state.inputTransitioning} timeout={500} classNames="fade-rtl">
                  <input
                    ref={this.state.inputRef}
                    id={styles.input}
                    {...this.state.inputParams[this.state.currentInput]}
                  />
                </CSSTransition>
                {this.state.error.occurred ? <p className="error">{this.state.error.message}</p> : null}
                <button id={styles.submit} className="box-shadow" type="submit">Enter</button>
              </form>
            </div>
          </CSSTransition>
        </div>
      </div>
    )
  }
}

export default Lobby;
