import React from "react";
import CSS from "csstype";
import style from "./Answering.module.scss";
import NakoAPI, {
  QuestionInfo,
  QuestionEndRequest,
} from "../../modules/websocket-messages";
import userAnswer from "./answering_MultipleChoice";

export interface Props {
  correctAnswer?: QuestionEndRequest;
  connection: NakoAPI;
  questionInfo: QuestionInfo<"student">;
  currentQuestionProps: number;
}

interface States {
  userAnswer: userAnswer;
}

class MCModule extends React.Component<Props, States> {
  constructor(props: Props) {
    super(props);
  }

  normalAnswerStyle: CSS.Properties = {
    width: "45%",
    maxHeight: "50em",
    marginTop: "3%",
    flexShrink: 1,
    flexGrow: 1,
    color: "black",
    fontSize: "1.6em",
    textAlign: "center",
    padding: "2%",
    borderRadius: "50px",
    border: "7px solid #4ed02c",
    background: "white",
    boxShadow:
      "10px 10px 10px rgba(0, 0, 0, 0.45),-10px -10px 10px rgba(255, 255, 255, 0.45)",
    transitionDuration: "0.3s",
    outline: "none",
  };

  correctAnswerStyle: CSS.Properties = {
    width: "45%",
    maxHeight: "50em",
    marginTop: "3%",
    flexShrink: 1,
    flexGrow: 1,
    color: "black",
    fontSize: "1.6em",
    textAlign: "center",
    padding: "2%",
    borderRadius: "50px",
    border: "7px solid black",
    background: "white",
    boxShadow:
      "10px 10px 10px rgba(0, 0, 0, 0.45),-10px -10px 10px rgba(255, 255, 255, 0.45)",
    transitionDuration: "0.3s",
    outline: "none",
  };

  wrongAnswerStyle: CSS.Properties = {
    width: "45%",
    maxHeight: "50em",
    marginTop: "3%",
    flexShrink: 1,
    flexGrow: 1,
    color: "black",
    fontSize: "1.6em",
    textAlign: "center",
    padding: "2%",
    borderRadius: "50px",
    border: "7px solid red",
    background: "white",
    boxShadow:
      "10px 10px 10px rgba(0, 0, 0, 0.45),-10px -10px 10px rgba(255, 255, 255, 0.45)",
    transitionDuration: "0.3s",
    outline: "none",
  };

  render() {
    return (
      <div id={style["muti-wrapper"]}>
        {this.props.questionInfo.choices.map((value, index) => {
          return (
            <button
              id={style["button"]}
              disabled={true}
              key={index}
              onClick={() => {
                this.props.connection.send({
                  method: "req",
                  type: "answer_question",
                  payload: {
                    choice: this.state.userAnswer,
                    currentQuestionCheck: this.props.currentQuestionProps,
                  },
                });
                this.setState(
                  {
                    userAnswer: index,
                    changeColor: this.state.changeColor + 1,
                  },
                  () => {
                    console.log(this.state.userAnswer);
                  }
                );
              }}
              style={
                this.props.correctAnswer?.correctAnswer === undefined
                  ? this.normalAnswerStyle
                  : this.props.correctAnswer?.correctAnswer === index
                  ? this.correctAnswerStyle
                  : this.wrongAnswerStyle
              }
            >
              {value}
            </button>
          );
        })}
      </div>
    );
  }
}

export default MCModule;
