import React from "react";
import { CSSTransition } from "react-transition-group";
import NakoAPI, {
  GameInfo,
  QuestionEndRequest,
} from "../../modules/websocket-messages";
import { animate } from "../../utils";
import AnsweringMC from "../AnsweringMC/answering_MultipleChoice";
import styles from "./game.module.scss";

interface GameProps extends React.HTMLAttributes<HTMLDivElement> {
  connection: NakoAPI;
  gameInfo: GameInfo;
}

interface GameState {
  questionCount: number;
  currentQuestion: number;
  currentPhase: GamePhase;
  questionNumberElement: React.RefObject<HTMLHeadingElement>;
  onSlideAnimComplete?: (value: void | PromiseLike<void>) => void;
  correctAnswer?: QuestionEndRequest;
}

enum GamePhase {
  Transition,
  Active,
  Ended,
}

export default class Game extends React.Component<GameProps, GameState> {
  constructor(props: GameProps) {
    super(props);

    this.state = {
      questionCount: 0,
      currentQuestion: 0,
      currentPhase: GamePhase.Transition,
      questionNumberElement: React.createRef<HTMLHeadingElement>(),
    };

    // Gameplay loop
    this.props.connection.subscribe("req", "game_update", async (msg) => {
      this.setState({ currentPhase: GamePhase.Transition });

      await new Promise<void>((res) =>
        this.setState({ onSlideAnimComplete: res }, () =>
          console.log("Waiting for animation to complete")
        )
      );

      this.setState({
        questionCount: this.state.questionCount + 1,
        currentQuestion: msg.currentQuestion,
      });

      if (this.state.questionNumberElement.current) {
        await animate("bounce", this.state.questionNumberElement.current, 500);
      }

      this.props.connection.send({
        method: "res",
        type: "game_update",
        payload: null,
      });

      this.props.connection.subscribe("req", "question_start").then(() => {
        this.props.connection.send({
          method: "res",
          type: "question_start",
          payload: null,
        });

        console.log("Question started");
        this.setState({ currentPhase: GamePhase.Active });

        this.props.connection.subscribe("req", "question_end").then((msg) => {
          this.props.connection.send({
            method: "res",
            type: "question_end",
            payload: null,
          });

          console.log("Question ended");
          this.setState({ correctAnswer: msg });
        });
      });
    });

    this.props.connection.subscribe("req", "game_end").then((msg) => {
      this.props.connection.send({
        method: "res",
        type: "game_end",
        payload: null,
      });

      console.log("Game ended");
      console.log(msg);
      this.setState({ currentPhase: GamePhase.Ended });
    });
  }

  render() {
    const { connection, gameInfo, ...rest } = this.props;
    let renderTarget: JSX.Element;

    switch (this.state.currentPhase) {
      case GamePhase.Transition:
        renderTarget = (
          <div className="fill-parent" id={styles.transition}>
            <CSSTransition
              in={true}
              appear={true}
              exit={false}
              timeout={700}
              classNames={{
                appear: styles["slide-in-appear"],
                appearActive: styles["slide-in-appear-active"],
                appearDone: styles["slide-in-appear-done"],
              }}
              onEntered={() => {
                console.log("Animation completed");

                if (this.state.onSlideAnimComplete) {
                  this.state.onSlideAnimComplete();
                } else {
                  console.warn("There was no promise to resolve.");
                }
              }}
            >
              <span>
                <h1>Question </h1>
                <h1 ref={this.state.questionNumberElement}>
                  {this.state.questionCount.toString()}
                </h1>
              </span>
            </CSSTransition>
          </div>
        );
        break;

      case GamePhase.Active:
        renderTarget = (
          <AnsweringMC
            connection={this.props.connection}
            questionInfo={
              this.props.gameInfo.questions[this.state.currentQuestion]
            }
            correctAnswer={this.state.correctAnswer}
            currentQuestionProps={this.state.currentQuestion}
          />
        );
        break;

      default:
        renderTarget = <div className="fill-parent">TODO: Result UI</div>;
        break;
    }

    return <div {...rest}>{renderTarget}</div>;
  }
}
