import React from "react";
import * as Device from "react-device-detect";
import ReactDOM from "react-dom";
import { BrowserRouter as Router, Link, Redirect, Route, Switch } from "react-router-dom";
import { imagesWithAltText } from "./assets/images/test";
import Lobby from "./desktop/lobby/lobby";
import Login from "./desktop/login/login";
import Options from "./desktop/Options/options";
import Profile from "./desktop/profile/profile";
import Register from "./desktop/register/register";
import "./index.scss";
import MLogin from "./mobile/login/login";
import { AccountInfo } from "./modules/account-info";

if (Device.isBrowser) {
  import("./font-desktop.scss");
} else {
  import("./font-mobile.scss");
}

interface AppState {
  accountInfo?: AccountInfo;
}

class App extends React.Component<{}, AppState> {
  constructor(props: {}) {
    super(props);

    this.state = {};
  }

  render() {
    return (
      <Router>
        <Switch>
          <Route exact path="/">
            {/* <Link to="/options">
              <p>Options</p>
            </Link> */}
            <Link to="/play">
              <p>Lobby</p>
            </Link>
            <Link to="/login">
              <p>Login</p>
            </Link>
            <Link to="/register">
              <p>Register</p>
            </Link>
          </Route>
          <Route path="/options">
            <Options pictures={[imagesWithAltText, imagesWithAltText]} />
          </Route>
          <Route path="/play">
            <Lobby className="lobby-component fill-parent" />
          </Route>
          <Route path="/login">
            {this.state.accountInfo ?
            <Redirect to="/profile"/> :
            Device.isBrowser ?
            <Login className="fill-parent" onLoggedIn={accountInfo => this.setState({ accountInfo: accountInfo })}/> :
            <MLogin className="m-login-component fill-parent"/>}
          </Route>
          <Route path="/register">
            <Register className="fill-parent"></Register>
          </Route>
          <Route path="/profile">
            {i => this.state.accountInfo ? <Profile className="fill-parent" accountInfo={this.state.accountInfo} {...i}/> : <Redirect to="/login"/>}
            {/* {i => <Profile className="fill-parent" accountInfo={{ name: "Test", role: "student" }} {...i}/>} */}
          </Route>
          <Redirect to="/login"/>
        </Switch>
      </Router>
    )
  }
}

ReactDOM.render(
    <App />,
  document.getElementById("root")
);
