import React from "react";
import CSS from "csstype";
import style from "./AnsweringMC.module.scss";
import NakoAPI, {
  QuestionInfo,
  QuestionEndRequest,
  Question,
} from "../../modules/websocket-messages";

export interface Props {
  correctAnswer?: QuestionEndRequest;
  connection: NakoAPI;
  questionInfo: QuestionInfo<"student", Question<"multiple_choice">>;
  currentQuestionProps: number;
  onAnswerSelected: (userAnswer: number) => void;
}

interface States {
  userAnswer: number;
  sending: boolean;
}

class MCModule extends React.Component<Props, States> {
  constructor(props: Props) {
    super(props);
    this.state = {
      userAnswer: 0,
      sending: false,
    };
  }

  normalAnswerStyle: CSS.Properties = {
    width: "45%",
    maxHeight: "50em",
    marginTop: "3%",
    flexShrink: 1,
    flexGrow: 1,
    color: "black",
    fontSize: "1.6em",
    textAlign: "center",
    padding: "2%",
    borderRadius: "50px",
    border: "7px solid #4ed02c",
    background: "white",
    boxShadow:
      "10px 10px 10px rgba(0, 0, 0, 0.45),-10px -10px 10px rgba(255, 255, 255, 0.45)",
    transitionDuration: "0.3s",
    outline: "none",
  };

  correctAnswerStyle: CSS.Properties = {
    width: "45%",
    maxHeight: "50em",
    marginTop: "3%",
    flexShrink: 1,
    flexGrow: 1,
    color: "black",
    fontSize: "1.6em",
    textAlign: "center",
    padding: "2%",
    borderRadius: "50px",
    border: "7px solid black",
    background: "white",
    boxShadow:
      "10px 10px 10px rgba(0, 0, 0, 0.45),-10px -10px 10px rgba(255, 255, 255, 0.45)",
    transitionDuration: "0.3s",
    outline: "none",
  };

  wrongAnswerStyle: CSS.Properties = {
    width: "45%",
    maxHeight: "50em",
    marginTop: "3%",
    flexShrink: 1,
    flexGrow: 1,
    color: "black",
    fontSize: "1.6em",
    textAlign: "center",
    padding: "2%",
    borderRadius: "50px",
    border: "7px solid red",
    background: "white",
    boxShadow:
      "10px 10px 10px rgba(0, 0, 0, 0.45),-10px -10px 10px rgba(255, 255, 255, 0.45)",
    transitionDuration: "0.3s",
    outline: "none",
  };

  Classify = (e: number | number[]) => {};

  CSSPicker = (index: number) => {
    if (
      this.props.correctAnswer?.correctAnswer === undefined &&
      this.state.sending !== true
    ) {
      return this.normalAnswerStyle;
    }
    if (
      this.props.correctAnswer?.correctAnswer === undefined &&
      this.state.sending === true &&
      this.state.userAnswer === index
    ) {
      return this.UserAnswerStyle;
    } else if (
      this.props.correctAnswer?.correctAnswer === undefined &&
      this.state.sending === true &&
      this.state.userAnswer !== index
    ) {
      return this.normalAnswerStyle;
    }
    if (
      this.props.correctAnswer?.correctAnswer === index &&
      this.state.sending === true
    ) {
      return this.correctAnswerStyle;
    } else if (
      this.props.correctAnswer?.correctAnswer !== index &&
      this.state.sending === true
    ) {
      return this.wrongAnswerStyle;
    }
  };

  render() {
    return (
      <div id={style["muti-wrapper"]}>
        {this.props.questionInfo.choices.map((value, index) => {
          return (
            <button
              id={style["button"]}
              //disabled={true}
              key={index}
              onClick={() => {
                this.setState({ userAnswer: index }, () => {
                  this.props.onAnswerSelected(this.state.userAnswer);
                });
                console.log("userAnswer: " + this.state.userAnswer);
              }}
              style={
                this.props.correctAnswer?.correctAnswer === undefined
                  ? this.normalAnswerStyle
                  : this.props.correctAnswer?.correctAnswer === index
                  ? this.correctAnswerStyle
                  : this.wrongAnswerStyle
              }
            >
              {value}
            </button>
          );
        })}
      </div>
    );
  }
}

export default MCModule;
