import React from "react";
import { Link, Route, RouteChildrenProps } from "react-router-dom";
import { AccountInfo } from "../../modules/account-info";
import Logo from "../../modules/logo/logo";
import styles from "./teacher.module.scss";
import threeline from "./Group 842.svg";

interface ProfileProps {
  accountInfo: AccountInfo;
}

interface ProfileState {
  resizeObserver: ResizeObserver;
  targetTopBarHeight: number;
  dataSet: {
    name: string;
    type: string;
    teacher: string;
  }[];
}

export class Misstea extends React.Component<
  ProfileProps & React.HTMLAttributes<HTMLDivElement> & RouteChildrenProps,
  ProfileState
> {
  constructor(
    props: ProfileProps &
      React.HTMLAttributes<HTMLDivElement> &
      RouteChildrenProps
  ) {
    super(props);

    this.state = {
      resizeObserver: new ResizeObserver((entries) => {
        for (let i of entries) {
          this.setState({ targetTopBarHeight: i.target.clientHeight });
        }
      }),
      targetTopBarHeight: 0,
      dataSet: [
        {
          name: "Game 10",
          type: "Boss Fight",
          teacher: "Sum Ting Wong",
        },
        {
          name: "Game 11",
          type: "Boss Fight",
          teacher: "Amy Wong",
        },
        {
          name: "Game 12",
          type: "Boss Fight",
          teacher: "Lee Man Ho",
        },
        {
          name: "Game 13",
          type: "Boss Fight",
          teacher: "Lee Man Ho",
        },
        {
          name: "Game 14",
          type: "Boss Fight",
          teacher: "Amy Wong",
        },
        {
          name: "Game 15",
          type: "Boss Fight",
          teacher: "Sum Ting Wong",
        },
        {
          name: "Game 16",
          type: "Boss Fight",
          teacher: "Sum Ting Wong",
        },
        {
          name: "Game 17",
          type: "Boss Fight",
          teacher: "Lee Man Ho",
        },
        {
          name: "Game 18",
          type: "Boss Fight",
          teacher: "Amy Wong",
        },
        {
          name: "Game 19",
          type: "Boss Fight",
          teacher: "Sum Ting Wong",
        },
        {
          name: "Game 20",
          type: "Boss Fight",
          teacher: "Lee Man Ho",
        },
        {
          name: "Game 21",
          type: "Boss Fight",
          teacher: "Amy Wong",
        },
        {
          name: "Game 22",
          type: "Boss Fight",
          teacher: "Lee Man Ho",
        },
        {
          name: "Game 23",
          type: "Boss Fight",
          teacher: "Sum Ting Wong",
        },
        {
          name: "Game 24",
          type: "Boss Fight",
          teacher: "Amy Wong",
        },
      ],
    };
  }

  componentWillUnmount() {
    this.state.resizeObserver.disconnect();
  }

  render() {
    const { accountInfo, location, history, match, ...rest } = this.props;

    return (
      <div {...rest}>
        <div id={styles["profile-container"]} className="fill-parent">
          <div>
            <Logo
              elementRefUpdate={(element) =>
                this.state.resizeObserver.observe(element)
              }
              id={styles.logo}
              role={this.props.accountInfo.role ?? "teacher"}
              primaryColor="#424242"
              secondaryColor="#424242"
            />
            <Link
              to={"/teacher"}
              className={
                location.pathname === "/teacher" ? styles.selected : undefined
              }
            >
              Home
            </Link>
            {["Edit Game", "Student", "Set Awards", "Settings"].map((i) => (
              <Link
                key={i}
                to={`/teacher/${i.toLowerCase()}`}
                className={
                  location.pathname.includes(i.toLowerCase())
                    ? styles.selected
                    : undefined
                }
              >
                {i}
              </Link>
            ))}
          </div>
          <div>
            <div
              id={styles["top-bar"]}
              style={{
                height: `calc(4rem + ${this.state.targetTopBarHeight}px)`,
              }}
            >
              <span>
                <img className={styles.avatar} alt="profile" />
                {this.props.accountInfo.name}
              </span>
            </div>
            <Route exact path="/teacher">
              <div id={styles.thome}>
                <div id={styles.tevent}>
                  <h2>Boss Fight</h2>
                  <button>Start</button>
                </div>

                <div id={styles.tawards}></div>
                <div id={styles.tunknown}></div>
                <div id={styles.tsocial}></div>
                <div id={styles.tmatchmaking}>
                  <Link to="/play">Matching</Link>
                </div>
              </div>
            </Route>
            <Route path="/teacher/Edit Game">
              <div id={styles.edit}>
                <Link to="">
                  <div className={styles.button}>
                    <button className={styles.button2}>Add Game</button>
                  </div>
                </Link>
                <div className={styles["flex-container"]}>
                  <div id={styles.whitebar}>
                    <div id={styles.name}>Name</div>

                    <div id={styles.type}> Type</div>

                    <div id={styles.teacher}>Teacher </div>
                  </div>
                  <div id={styles.bluesheet}>
                    {console.log(this.state.dataSet)}
                    {this.state.dataSet.map((value, index) => {
                      return (
                        <div key={index} className={styles.yellowbar}>
                          <div key={index} className={styles.game10}>
                            {value.name}
                          </div>
                          <div key={index} className={styles.boss}>
                            {value.type}
                          </div>
                          <div key={index} className={styles.teachname}>
                            {value.teacher}
                          </div>
                          <img
                            key={index}
                            src={threeline}
                            className={styles.threeline}
                          ></img>
                        </div>
                      );
                    })}
                  </div>
                </div>
              </div>
            </Route>
            <Route path="/teacher/Edit Game/QuestionBank"></Route>
            <Route path="/teacher/Student">
              <span>Games</span>
            </Route>
            <Route path="/teacher/Set Awards">
              <span>Awards</span>
            </Route>
            <Route path="/teacher/Settings">
              <span>Settings</span>
            </Route>
          </div>
        </div>
      </div>
    );
  }
}

export default Misstea;
