import React from "react";
import NakoAPI, { GameInfo } from "../../modules/websocket-messages";
import { delay } from "../../utils";
import AnsweringMC from "../Answering(MC)/answering(MultipleChoice)";

interface GameProps extends React.HTMLAttributes<HTMLDivElement> {
  connection: NakoAPI;
  gameInfo: GameInfo;
}

interface GameState {
  questionCount: number;
  currentQuestion: number;
  currentPhase: GamePhase;
}

enum GamePhase {
  Transition,
  Active
}

export default class Game extends React.Component<GameProps, GameState> {
  constructor(props: GameProps) {
    super(props);

    this.state = { questionCount: 0, currentQuestion: 0, currentPhase: GamePhase.Transition };

    this.props.connection.subscribe("req", "game_update", async msg => {
      this.setState({ currentPhase: GamePhase.Transition });
      await delay(500);
      this.setState({ questionCount: this.state.questionCount + 1, currentQuestion: msg.currentQuestion });

      this.props.connection.subscribe("req", "question_start").then(() => {
        this.setState({ currentPhase: GamePhase.Active });

        this.props.connection.subscribe("req", "question_end").then(() => {
          // Do something...
        })
      });
    });
  }

  render() {
    const { connection, gameInfo, ...rest } = this.props;

    return (
      <div {...rest}>
        {this.state.currentPhase === GamePhase.Transition ?
        <div className="fill-parent">
          Question {this.state.questionCount.toString()}
        </div> :
        <AnsweringMC {...this.props.gameInfo.questions[this.state.currentQuestion]}/>
        }
      </div>
    )
  }
}
